import React, { Component } from 'react';
import { Card, Button, Col, Row, Form, Container } from 'react-bootstrap';
import 'jodit';
import 'jodit/build/jodit.min.css';
import JoditEditor from "jodit-react";
import axios from 'axios';
import cookie from 'universal-cookie';
const Cookie = new cookie();

class Post extends Component {
    constructor(props) {
        super(props);
        this.updateContent= this.updateContent.bind(this);
        this.pushpost=this.pushpost.bind(this);
        this.state = {
            title: '',
            content: ''
        }
    }

    async pushpost() {
        await axios({
            method: 'POST',
            url:'/post/postnews',
            data: {
                title: this.state.title,
                content: this.state.content,
                token: Cookie.get('token')
            }
        }).then(x => console.log(x))
    }
    updateContent = (value) => {
        this.setState({ content: value })
    }
    jodit;
    setRef = jodit => this.jodit = jodit;

    config = {
        readonly: false,
        "autofocus": true,
        "uploader": {
            "insertImageAsBase64URI": true
        },
        "enter": "P",
        "toolbarSticky": false,
        "buttons": "|,bold,underline,italic,|,|,ul,ol,|,|,fontsize,brush,paragraph,,image,table,link,|,align,undo,redo,\n,|,fullsize"
    }
    render() {

        return (
            <Form>
                <Card>
                    <Card.Header>
                        <Form.Control onChange={(value)=>this.setState({title:value})} type="text" placeholder="Title" />
                        <Row className='pt-3 m-0'>
                            <Col xs={11} className='p-0'>
                                <Form.Control type="text" placeholder="Tag" />
                            </Col>
                            <Col xs={1} className='p-0 pl-2'>
                                <Button onClick={this.pushpost} variant="primary">Đăng bài</Button>
                            </Col>
                        </Row>
                    </Card.Header>
                    <Card.Body className='h-100'>
                        <JoditEditor
                            editorRef={this.setRef}
                            value={this.state.content}
                            config={this.config}
                            onChange={this.updateContent}
                        />
                    </Card.Body>
                </Card>
                <Container>

                </Container>
            </Form>
        );
    }
}

export default Post;
