import React, { Component } from 'react';
import { Card, Col, Image, Row } from 'react-bootstrap';
import { Link, withRouter } from 'react-router-dom';
import HTMLPraser from 'react-html-parser'
class Info_user extends Component {
    constructor(props) {
        super(props);
        this.state = {};
        this.renderText = this.renderText.bind(this)
    }

    renderText(value) {
        const div = document.createElement('div');
        div.innerHTML = (value)
        return div.innerText.slice(0,200)+"..."
    }
    render() {
        return (
            <Row className='flex-row mb-3 border-bottom '>
                <Image style={{ maxWidth: '37px', maxHeight: '37px' }} roundedCircle src='https://viblo.asia/images/mm.png'></Image>
                <Col>
                    <Link style={{ WebkitLineClamp: 2, WebkitBoxOrient: 'vertical', overflow: 'hidden', display: '-webkit-box' }}
                        to={`/?id=${this.props.id}`}
                        className="h3 text-left">{this.props.title}</Link>
                    <Card.Body className='p-1' style={{ WebkitLineClamp: 2, maxHeight: '150px', WebkitBoxOrient: 'vertical', overflow: 'hidden', display: '-webkit-box' }}>
                        {this.renderText(this.props.content)}
                    </Card.Body>
                </Col>
            </Row>

        );
    }
}

export default withRouter(Info_user);
