import React, { Component } from 'react';
import { Card, Col, Image, Row } from 'react-bootstrap';
import { Link } from 'react-router-dom';
class Info_user extends Component {
    constructor(props) {
        super(props);
        this.state = {}
    }
    render() {
        return (
            <Row style={{ height: '150px' }} className='flex-row mb-3 border-bottom '>
                <Image style={{ maxWidth: '37px', maxHeight: '37px' }} roundedCircle src='https://viblo.asia/images/mm.png'></Image>
                <Col>
                    <Link to='/post' className="h3 text-left">{this.props.title}</Link>
                    <Card.Body className='p-1' style={{ WebkitLineClamp: 2,WebkitBoxOrient:'vertical',overflow:'hidden',display:'-webkit-box' }}>{this.props.content}</Card.Body>
                </Col>
            </Row>

        );
    }
}

export default Info_user;
