import React, { Component } from 'react';
import { Card, Button, Col, Row, Form, Container } from 'react-bootstrap';
import CKEditor from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import ReactHtmlParser from 'react-html-parser';

class Post extends Component {
    constructor(props){
        super(props);
        this.state={
            title:'',
            tag:'',
            content:''
        }
    }
    render() {
        return (
            <Form>
                <Card>
                    <Card.Header>
                        <Form.Control type="text" placeholder="Title" />
                        <Row className='pt-3 m-0'>
                            <Col xs={11} className='p-0'>
                                <Form.Control type="text" placeholder="Tag" />
                            </Col>
                            <Col xs={1} className='p-0 pl-2'>
                                <Button variant="primary">Đăng bài</Button>
                            </Col>
                        </Row>
                    </Card.Header>
                    <Card.Body className='h-100'>
                        <CKEditor
                            editor={ClassicEditor}
                            config={{ placeholder: 'Tạo bài viết mới !' }}

                            onChange={(event, editor) => {
                                const data = editor.getData();
                                this.setState({content:data})
                                console.log(this.state.content)
                            }}
                        />
                    </Card.Body>
                </Card>
                <Container>
                {ReactHtmlParser(this.state.content)}
                </Container>
            </Form>
        );
    }
}

export default Post;
