import React, { Component } from 'react';
import { Col, Container,Image } from 'react-bootstrap'
import axios from 'axios';
import cookie from 'universal-cookie';
import { Redirect } from 'react-router-dom'
import Info_post from './Info_post';
import Img_loading from '../../asset/imageloading.gif';
import { Row } from 'react-bootstrap';
const Cookie = new cookie();


class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            info_post: [],
            loading: true
        }
    }

    async componentDidMount() {
        let poster = await axios.get('/post/postnews')
        if (poster.status === 200) {
            this.setState({ info_post: poster.data.info, loading: true })
            console.log(this.state)
        }
        else
            this.props.history.push('/badrequest')
    }
    render() {
        return (
            <Container >
                <Row className='flex-column mt-3'>
                    <Col lg={8} xs={12}>
                        {!this.state.loading ?
                            this.state.info_post.map((x) => <Info_post
                                key={x.id} title={x.title}
                                id={x.id} author={x['User_login.info_author']}
                                content={x.content}
                                create_at={x.createdAt}
                            ></Info_post>) : <Image src={Img_loading}></Image>
                        }
                    </Col>
                </Row>
            </Container>
        );
    }
}
export default Home;
