import React, { Component } from 'react';
import { Card, Col, Image, Row } from 'react-bootstrap';
import { Link,withRouter } from 'react-router-dom';
import HTMLPraser from 'react-html-parser'
class Info_user extends Component {
    constructor(props) {
        super(props);
        this.state = {}
    }
    render() {
        
        return (
            <Row  className='flex-row mb-3 border-bottom '>
                <Image style={{ maxWidth: '37px', maxHeight: '37px' }} roundedCircle src='https://viblo.asia/images/mm.png'></Image>
                <Col>
                    <Link to={`/Lập trình mobile`} className="h3 text-left">{this.props.title}</Link>
                    <Card.Body className='p-1' style={{ WebkitLineClamp: 2,height:'150px', WebkitBoxOrient: 'vertical', overflow: 'hidden', display: '-webkit-box' }}>
                        {HTMLPraser(this.props.sub_title)}
                    </Card.Body>
                </Col>
            </Row>

        );
    }
}

export default withRouter(Info_user);
