import React, { Component } from 'react';
import { Container, Alert, Navbar, InputGroup, FormControl, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom'
class User extends Component {
    constructor(props) {
        super(props);
        this.state = { email:'ngodinhvuluan@gmail.com',email_enable:true }
        this.handEmailOnChange= this.handEmailOnChange.bind(this);
    }
    
    handEmailOnChange(e){
        this.setState({email:e.target.value})
    }
    render() {
        console.log(this.props)
        return (
            <Container>
                <Alert className='h3' variant="primary">Thông tin tài khoản
                <Link to={this.props.match.url}>{this.props.match.params.user_id}</Link>
                </Alert>
                <Navbar bg='light'>
                    <InputGroup className="mb-3">
                        <InputGroup.Prepend>
                            <InputGroup.Text>Email</InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl 
                        aria-label="Small" 
                        onChange={this.handEmailOnChange} 
                        value={this.state.email}
                        disabled={this.state.email_enable} 
                        aria-describedby="inputGroup-sizing-sm" />
                    </InputGroup>
                    <Button onClick={()=>this.setState({email_enable:false})}></Button>
                </Navbar>
            </Container>
        );
    }
}

export default User;
