import React, { Component } from 'react';
import Navbar from './Navbar';
import { Container, Image, Row, Col, Card } from 'react-bootstrap'
import axios from 'axios';
import cookie from 'universal-cookie';
import { BrowserRouter as Router, Route, Link, NavLink, Switch } from "react-router-dom";
import { withRouter } from 'react-router-dom';
import Info_user from './Info_user';
const Cookie = new cookie();


class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            info_post: [],
            status_login: true
        }
    }
    async componentDidMount() {
        let poster = await axios.get('/post/postnews')
        this.setState({ info_post: poster.data.info })
        console.log(this.state)
        if (Cookie.get('token')) {
            await axios({
                method: 'post',
                url: '/user/verify',
                data: { "token": Cookie.get('token') }
            }).catch(err => {
                this.setState({ status_login: false })
                console.log(err)
            })

        } else {
            this.setState({ status_login: false })
        }
    }
    render() {
        return (
            <Container fluid>

                <Navbar login={this.state.status_login}>

                </Navbar>

                <Row>
                    <Col>
                        <Image fluid src='https://images.viblo.asia/full/5ce3a357-fe69-4a26-8b31-be94aabbf254.png' rounded />
                    </Col>
                </Row>
                <Container>
                        <Route path='/' exact render={
                            () =>
                                <Row className='flex-column mt-3 pl-3 pr-3'>
                                    <Col lg={8} xs={12}>
                                        {
                                            this.state.info_post.map(x => <Info_user key={x.id} title={x.title} content={x.content}></Info_user>)
                                        }
                                    </Col>
                                </Row>
                        }></Route>
                        <Route path='/flow' render={() => <p>Thon tin o day</p>}></Route>
                 
                </Container>
            </Container >
        );
    }
}

export default withRouter(Home);
