import React, { Component } from 'react';
import { Card, Button, Col, Row, Form, Container } from 'react-bootstrap';
import {Editor, EditorState} from 'draft-js';
import ReactHtmlParser from 'react-html-parser';
import axios from 'axios';

class Post extends Component {
    constructor(props) {
        super(props);
        this.state = {editorState: EditorState.createEmpty()};
        this.onChange = editorState => this.setState({editorState});
      } 
    
    async pushpost() {
   
    }
    render() {
        return (
            <Form>
                <Card>
                    <Card.Header>
                        <Form.Control type="text" placeholder="Title" />
                        <Row className='pt-3 m-0'>
                            <Col xs={11} className='p-0'>
                                <Form.Control type="text" placeholder="Tag" />
                            </Col>
                            <Col xs={1} className='p-0 pl-2'>
                                <Button onSubmit={this.pushpost()} variant="primary">Đăng bài</Button>
                            </Col>
                        </Row>
                    </Card.Header>
                    <Card.Body className='h-100'>
                    <Editor editorState={this.state.editorState} onChange={this.onChange} />
                    </Card.Body>
                </Card>
                <Container>
                    {ReactHtmlParser(this.state.content)}
                </Container>
            </Form>
        );
    }
}

export default Post;
