import React, { Component } from 'react';
import PraseHTML from 'react-html-parser';
import {Container} from 'react-bootstrap';
import axios from 'axios';
import { withRouter } from 'react-router-dom'
import HtmlParser from 'react-html-parser';
class Info_post extends Component {
    constructor(props) {
        super(props);
        this.state = { info: null }
    }
    componentDidMount() {
        axios({
            method: "GET",
            url: '/post/postnewid',
            params: { id: this.props.match.params.post_id }
        }).then(x => {
            this.setState({info:x.data.info[0]})
            console.log(x.data)
        }).catch(err => console.log(err))
    }
    render() {
        console.log(this.state.info)
        return (
            <Container>
                <Row>
{this.state.info.title}
                </Row>
                {HtmlParser(this.state.info.content)}
            </Container>
        );
    }
}

export default withRouter(Info_post);
