import React, { Component } from 'react';
import PraseHTML from 'react-html-parser';
import axios from 'axios';
import { withRouter } from 'react-router-dom'
import HtmlParser from 'react-html-parser';
class Info_post extends Component {
    constructor(props) {
        super(props);
        this.state = { info: null }
    }
    componentDidMount() {
        axios({
            method: "GET",
            url: '/post/postnewid',
            params: { id: this.props.match.params.post_id }
        }).then(x => {
            this.setState({info:x.data.info})
            console.log(x.data.info[0].content)
        }).catch(err => console.log(err))
    }
    render() {
        console.log(this.props.match.params.post_id)
        return (
            <div>
                {JSON.stringify(this.props.match)}
                {HtmlParser(this.state.info)}
            </div>
        );
    }
}

export default withRouter(Info_post);
