import React, { Component } from 'react';
import { Card, Button, Col, Alert, Row, Form, Container } from 'react-bootstrap';
import 'jodit';
import 'jodit/build/jodit.min.css';
import JoditEditor from "jodit-react";
import axios from 'axios';
import { withFormik } from 'formik';
import cookie from 'universal-cookie';
import { withRouter } from 'react-router-dom';
import * as Yup from 'yup';

const Cookie = new cookie();


class Post extends Component {
    constructor(props) {
        super(props);
        this.updateContent = this.updateContent.bind(this);
        this.pushpost = this.pushpost.bind(this);

        this.handleSubTitleChange = this.handleSubTitleChange.bind(this)
        this.state = {
            title: '',
            content: '',
            sub_title: ''
        }
    }

    async pushpost() {
        if (Object.keys(this.props.errors).length <= 0) {
            await axios({
                method: 'POST',
                url: '/post/postnews',
                data: {
                    title: this.props.values.title,
                    sub_title: this.state.sub_title,
                    content: this.state.content,
                    token: Cookie.get('token')
                }
            }).then(x => {
                if (x.data.statuscode === 200) {
                    this.props.history.push('/')
                }
            })
                .catch(err => {
                    console.log(err)
                    this.setState({ res: true, desc: 'Có lỗi xảy ra' })
                    console.log(this.state)
                })
        }

    }
    handleSubTitleChange = (event) => {
        this.setState({ sub_title: event.target.value })
    }
    updateContent = (value) => {
        this.setState({ content: value })
    }
    jodit;
    setRef = jodit => this.jodit = jodit;

    config = {
        "readonly": false,
        "autofocus": true,
        "uploader": {
            "insertImageAsBase64URI": true
        },
        "enter": "P",
        "toolbarSticky": false,
        "buttons": "|,bold,underline,italic,|,|,ul,ol,|,|,fontsize,paragraph,|,image,table,link,,align,undo,redo,\n,,hr,fullsize"
    }
    show(a) {
        this.setState({ ...this.state, res: a })
    }
    render() {
        const {
            handleChange,
            handleBlur,
            handleSubmit,
        } = this.props;
        console.log(this.props)
        return (
            <Form>
                {this.state.res === true &&
                    <Alert className="position-relative text-center"
                        style={{ width: '100%', zIndex: 10 }} onClose={() => this.show(false)} variant="danger" dismissible >{this.state.desc}</Alert>}
                <Card>
                    <Card.Header>
                        <Form.Control onChange={handleChange} onBlur={handleBlur} name='title' type="text" placeholder="Title" />
                        {this.props.touched.title && <Form.Text className="h6" style={{ color: 'red' }}> {this.props.errors.title}</Form.Text>}
                        <Row className='mt-3 m-0'>
                            <Col xs={11} className='p-0'>
                                <Form.Control name='tag' type="text" placeholder="Tag" />
                            </Col>
                            <Col xs={1} className='p-0 pl-2'>
                                <Button onClick={this.pushpost} variant="primary">Đăng bài</Button>
                            </Col>
                        </Row>
                    </Card.Header>
                    <Card.Body className='h-100'>
                        <JoditEditor
                            editorRef={this.setRef}
                            value={this.state.content}
                            tabIndex={1}
                            config={this.config}
                            onChange={this.updateContent}
                        />
                    </Card.Body>
                </Card>
                <Container>

                </Container>
            </Form>
        );
    }
}
const FormFormik = withFormik({
    mapPropsToValues: () => ({
        title: '',
        tag: '',
    }),
    validationSchema: (props) => {
        return Yup.object().shape({
            title: Yup.string().required("Vui lòng điền Title vào").min(5, "Title ít nhất 5 kí tự")
        })
    }
})(Post)
export default withRouter(FormFormik);
