import React, { Component } from 'react';
import { Row, Image, Col, Dropdown, Card, Button } from 'react-bootstrap';
import { TiInfoLarge, TiPencil } from 'react-icons/ti'
import { BsFillCircleFill } from 'react-icons/bs'
import { Link } from 'react-router-dom';

class Info_user extends Component {
    constructor(props) {
        super(props);
        this.state = { status: false }
        this.show = this.show.bind(this)
    }
    show(a) {
        this.setState({ status: a });
    };
    render() {

        console.log(this.props.data)
        return (
            <Row className='pl-4 align-items-center'>
                <Col className='pl-4 pr-0'>
                    <BsFillCircleFill className='position-absolute' style={{ margin: '-4px 12px', color: '#f56c6c' }} />
                    <span className='position-absolute' style={{ margin: '-4px 15px', fontSize: '14px', color: 'white' }}>4</span>
                    <TiInfoLarge size={30} />
                </Col>
                <Col className='p-0'>
                    <TiPencil></TiPencil>
                </Col>
                <Col className='p-0'>
                    <Dropdown>
                        <Dropdown.Toggle onClick={()=>this.show(false)} style={{ backgroundColor: 'transparent', borderColor: 'transparent' }} color='white'>
                            <Image style={{ height: '37px', width: '37px' }} roundedCircle src='https://cdn.viblo.asia/_nuxt/img/0bca52a.png'></Image>
                        </Dropdown.Toggle>

                        <Dropdown.Menu className={`pt-0 ${this.state.status && 'd-none'}`} style={{ width: 'max-content', right: '0%', left: 'unset' }}>
                            <Col className='pl-0 pr-0'>
                                <Card bg='light pt-0' style={{ maxWidth: '200px' }}>
                                    <Card.Header>
                                        <Row>
                                            <Col xs={3} className='m-auto'>
                                                <Image style={{ height: '37px', width: '37px' }} roundedCircle src='https://cdn.viblo.asia/_nuxt/img/0bca52a.png'></Image>
                                            </Col >
                                            <Col xs={9} className='text-left'>
                                                <p className='alert-link'>{this.props.data.user_name}</p>
                                                <p className=' h6 text-muted text-break'>{`@${this.props.data.email}`}</p>
                                                <Button variant="primary">Edit </Button>
                                            </Col>
                                        </Row>

                                    </Card.Header>
                                </Card>
                            </Col>
                            <Col className='dropdown-item'> <Link onClick={()=>this.show(true)} to='/profile'> Profile</Link></Col>
                            <Col className='dropdown-item' ><Link onClick={()=>this.show(true)} to='/public/post'> My Content</Link></Col>
                            <Col className='dropdown-item'> <Link onClick={()=>this.show(true)} to='/logout'> Log out</Link></Col>
                        </Dropdown.Menu>
                    </Dropdown>

                </Col>
            </Row >
        );
    }
}

export default Info_user;
