import React, { Component } from "react";
import {
  Container,
  Alert,
  Navbar,
  InputGroup,
  FormControl,
  Button,
  Col,
  Row,
} from "react-bootstrap";
import axios from "axios";
import cookie from "universal-cookie";

import { Link } from "react-router-dom";
const Cookie = new cookie();

class User extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      user_name: "",
      user_name_enable: true,
      email_enable: true,
    };
    this.handEmailOnChange = this.handEmailOnChange.bind(this);
  }
  componentDidMount() {
    axios
      .post("/user/verify", { token: Cookie.get("token") })
      .then((x) => {
        this.setState({ email: x.data.email });
        console.log(x.data);
      })
      .catch((err) => this.props.history.push("/badrequest"));
  }
  handEmailOnChange(e) {
    this.setState({ email: e.target.value });
  }
  render() {
    console.log(this.props);
    return (
      <Container>
        <Alert className="h3" variant="primary">
          Thông tin tài khoản
          <Link to={this.props.match.url}>
            {this.props.match.params.user_id}
          </Link>
        </Alert>
        <div bg="light">
          <Row>
            <Col xs={10}>
              <InputGroup className="mb-3">
                <InputGroup.Prepend>
                  <InputGroup.Text>Email</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl
                  type="email"
                  aria-label="Small"
                  onChange={this.handEmailOnChange}
                  value={this.state.email}
                  disabled={this.state.email_enable}
                  aria-describedby="inputGroup-sizing-sm"
                />
              </InputGroup>
            </Col>
            <Col xs={2}>
              <Button
                className="mb-3"
                onClick={() => this.setState({ email_enable: false })}
              >
                Thay đổi Email
              </Button>
            </Col>
          </Row>
          <Row>
            <Col xs={10}>
              <InputGroup className="mb-3">
                <InputGroup.Prepend>
                  <InputGroup.Text>User name</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl
                  type="email"
                  aria-label="Small"
                  onChange={this.handEmailOnChange}
                  value={this.state.email}
                  disabled={this.state.email_enable}
                  aria-describedby="inputGroup-sizing-sm"
                />
              </InputGroup>
            </Col>
            <Col xs={2}>
              <Button
                className="mb-3"
                onClick={() => this.setState({ email_enable: false })}
              >
                Thay đổi Email
              </Button>
            </Col>
          </Row>
        </div>
      </Container>
    );
  }
}

export default User;
