import React, { Component } from 'react';
import Navbar from './Navbar';
import { Container, Image, Row, Col } from 'react-bootstrap'
import axios from 'axios';
import cookie from 'universal-cookie';
import Cookies from 'universal-cookie';
import { withRouter } from 'react-router-dom';
import Info_user from './Info_user';
const Cookie = new cookie();


class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            info_post: [],
            status_login: true
        }
    }
    async componentDidMount() {
        let poster = await axios.get('http://localhost:3001/post/postnews')
        this.setState({ info_post: poster.data.info })
        console.log(this.state)
        if (Cookie.get('token')) {
            await axios({
                method: 'post',
                url: 'http://localhost:3001/user/verify',
                data: { "token": Cookie.get('token') }
            }).catch(err => {
                this.setState({ status_login: false })
                console.log(err)
            })

        } else {
            this.setState({ status_login: false })
        }
    }
    render() {
        return (
            <Container fluid>

                <Navbar login={this.state.status_login}>

                </Navbar>

                <Row>
                    <Col>
                        <Image fluid src='https://images.viblo.asia/full/5ce3a357-fe69-4a26-8b31-be94aabbf254.png' rounded />
                    </Col>
                </Row>
                <Row>
                    {
                        this.state.info_post.map(x=><Car>{x.id}</Car>)
                    }
                </Row>
            </Container >
        );
    }
}

export default withRouter(Home);
