import React, { Component } from 'react';
import { Card, Col, Image, Row } from 'react-bootstrap';
import { Link, withRouter } from 'react-router-dom';
import HTMLPraser from 'react-html-parser'
class Info_user extends Component {
    constructor(props) {
        super(props);
        this.state = {};
        this.renderContent = this.renderContent.bind(this)
        this.renderTitle = this.renderTitle.bind(this);
    }
    renderTitle(value) {
        const div = document.createElement('div');
        div.innerHTML = (value)
        return div.innerText.slice(0, 45) + "..."
    }
    renderContent(value) {
        const div = document.createElement('div');
        div.innerHTML = (value)
        return div.innerText.slice(0, 150) + "..."
    }
    render() {
        return (
            <Row className='flex-row mb-3 border-bottom '>
                <Image style={{ maxWidth: '37px', maxHeight: '37px' }} roundedCircle src='https://viblo.asia/images/mm.png'></Image>
                <Col>
                    <Link 
                        to={`/?id=${this.props.id}`}
                        className="h3 text-left">{this.renderTitle(this.props.title)}</Link>
                    <Card.Body className='p-1'>
                        {this.renderContent(this.props.content)}
                    </Card.Body>
                </Col>
            </Row>

        );
    }
}

export default withRouter(Info_user);
