import React, { Component } from 'react';
import 'jodit';
import 'jodit/build/jodit.min.css';


class TextAre extends Component {
    constructor(props) {
        super(props);
        this.updateContent = this.updateContent.bind(this)
        this.state = {
            content: '',
        }
    }

    updateContent(value) {
        this.setState({ content: value })
    } jodit;
    setRef = jodit => this.jodit = jodit;


    render() {
        return <JoditEditor
            editorRef={this.setRef}
            value={this.state.content}

            onChange={this.updateContent} editorRef={this.setRef}
            value={this.state.content}
            config={this.config}
            onChange={this.updateContent}
            config={{
                "autofocus": false,
                "uploader": {
                    "insertImageAsBase64URI": true
                },
                "buttons": "|,bold,underline,italic,eraser,|,|,ul,ol,|,,fontsize,paragraph,,image,table,link,align,undo,redo,fullsize"
            }}

        />
    }
}

export default TextAre;
