import React, { Component } from "react";
import {
  Container,
  Alert,
  Card,
  Image,
  Form,
  Button,
  Col,
  Row,
} from "react-bootstrap";
import axios from "axios";
import cookie from "universal-cookie";

import { Link } from "react-router-dom";
const Cookie = new cookie();

class User extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      user_name: "",
      user_name_enable: true,
      email_enable: true,
      url_image_avatar: "",
    };
    this.handEmailOnChange = this.handEmailOnChange.bind(this);
  }
  componentDidMount() {
    axios
      .post("/user/verify", { token: Cookie.get("token") })
      .then((x) => {
        this.setState({
          email: x.data.email,
          url_image_avatar: x.data.image_user,
        });
        console.log(x.data);
      })
      .catch((err) => this.props.history.push("/badrequest"));
  }
  handEmailOnChange(e) {
    this.setState({ email: e.target.value });
  }
  render() {
    console.log(this.props);
    return (
      <Container fluid>
        <Card className="mt-3">
          <Card.Body>
            <Alert className="h3" variant="primary">
              Thông tin tài khoản
              <Link to={this.props.match.url}>
                {" " + this.props.match.params.user_id}
              </Link>
            </Alert>
            <Alert variant="info" className="h3">
              <p className="text-center">Anh dai dien</p>
              <div
                style={{
                  display: "flex",
                  position: "relative",
                  justifyContent: "center",
                }}
              >
                <Image
                  roundedCircle
                  style={{ height: "150px", width: "150px" }}
                  src={this.state.url_image_avatar}
                ></Image>
                <Button
                  variant="secondary"
                  size="ms"
                  style={{ position: "absolute", bottom: 0, width: "120px" }}
                >
                  Doi anh
                </Button>
              </div>
              <Row>
                <Col>
                  <Form.Group controlId="formBasicEmail">
                    <Form.Label>Email address</Form.Label>
                    <Form.Control
                      type="email"
                      placeholder="Enter email"
                      disabled={this.state.email_enable}
                    >
                      <Button>Thay doior emal</Button>
                    </Form.Control>
                  </Form.Group>
                </Col>
              </Row>
            </Alert>{" "}
          </Card.Body>
        </Card>
      </Container>
    );
  }
}

export default User;
