import React, { Component } from 'react';
import Navbar from './Navbar';
import { Container, Image, Row, Col } from 'react-bootstrap'
import axios from 'axios';
import cookie from 'universal-cookie';
import Cookies from 'universal-cookie';
import { withRouter } from 'react-router-dom';
const Cookie = new cookie();


class Home extends Component {
    constructor(props) {
        super(props);
        this.state = { status_login: true }
    }
    async componentDidMount() {
        await axios.get('http://localhost:3001/post/postnews')
        if (Cookie.get('token')) {
            let poster = await axios({
                method: 'post',
                url: 'http://localhost:3001/user/verify',
                data: { "token": Cookie.get('token') }
            }).catch(err => {
                this.setState({ status_login: false })
                console.log(err)
            })

        } else {
            this.setState({ status_login: false })
        }
    }
    render() {
        return (
            <Container fluid>
                <Row>
                    <Navbar login={this.state.status_login}>

                    </Navbar>
                </Row>
                <Row>
                    <Col>
                        <Image src='https://images.viblo.asia/full/5ce3a357-fe69-4a26-8b31-be94aabbf254.png' rounded/> 
                    </Col>
                </Row>
            </Container >
        );
    }
}

export default withRouter(Home);
