import React, { Component } from 'react';
import Navbar from './Navbar';
import { Container, Col } from 'react-bootstrap'
import axios from 'axios';
import cookie from 'universal-cookie';
import { Route, Switch, Link } from "react-router-dom";
import { withRouter, BrowserRouter } from 'react-router-dom';
import Info_user from './Info_user';
const Cookie = new cookie();


class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            info_post: [],
            status_login: true
        }
    }

    async componentDidMount() {
        let poster = await axios.get('/post/postnews')
        this.setState({ info_post: poster.data.info })
        console.log(this.state)
        if (Cookie.get('token')) {
            await axios({
                method: 'post',
                url: '/user/verify',
                data: { "token": Cookie.get('token') }
            }).catch(err => {
                this.setState({ status_login: false })
                console.log(err)
            })

        } else {
            this.setState({ status_login: false })
        }
    }
    render() {
        console.log(this.props)
        return (
            <BrowserRouter>
                <Container fluid>
                    <Navbar login={this.state.status_login}></Navbar>
                    {this.props.children}
                    <Link to={`${this.props.match.url}/flow`}>thongtin</Link>
                    <Container className='flex-column mt-3 pl-3 pr-3'>
                        <Col lg={8} xs={12}>
                            {
                                this.state.info_post.map(x => <Info_user key={x.id} title={x.title} content={x.content}></Info_user>)
                            }
                        </Col>
                    </Container>
                    <Switch>
                        <Route path='/home/flow' exact render={() => <p>anananaanan</p>}></Route>
                    </Switch>
                </Container >
            </BrowserRouter>
        );
    }
}

export default withRouter(Home);
